def is_right(img_path):
  img = Image.open(img_path) 
  img = img.resize((128,128))
  
  # plt.imshow(img)
  img = np.array([np.array(img)])
  
  return np.argmax(wrong_right_classification.predict(img)[0])


def load_to_result(img_path):
  if is_right(img_path) ==0:
         # 사진을 잘못찍었을 경우
    return """
    <center>
      <h1>사진을 다시 찍어주시기 바랍니다.</h1>
      <br><br>
      <div>
      <h2>잘못 찍은 예시 : </h2><img src='data:image/jpeg;base64,'>
      <img src = 'data:image/jpeg;base64,'>
      </div>
      <br>
      <div>
      <h2>제대로 찍은 예시 : </h2><img src='data:image/jpeg;base64,'>
      <img src= 'data:image/jpeg;base64,'>
      </div>
       <a href ="/">홈으로 돌아가기 click</a>

    </center>
    """,'',''
  global ID
  img = Image.open(img_path) # 이미지 파일명 또는 파일 위치
  img = img.resize((128,128))
  img = np.array([np.array(img)])
  models = [model1,model2,model3,model4,model5,model6]
  to_check = ['미세각질','피지과다','모낭사이홍반','모낭홍반','비듬','탈모']
  results = []
  answer =""
  for m, c in zip(models,to_check):
    result = classes[np.argmax(m.predict(img)[0])]
    results.append(np.argmax(m.predict(img)[0])+1)
    answer+=f"{c} : {result} <br>"
  
  
  colors = []
  x = np.arange(len(to_check))
  for i in results:
    if i==1:
      colors.append('palegreen')
    elif i==2:
      colors.append('yellow')
    else:
      colors.append('orangered')
  plt.bar(x,results,align= 'edge', edgecolor = 'lightslategray',color=colors)
  plt.xticks(x,to_check)
  
  
  plt.xlabel('종류')
  plt.ylabel('위험도')
  plt.savefig(f'./static/result{ID}.png')
  link = f'static/result{ID}.png'
  #
  os.remove(img_path)
  plt.close()
  ID+=1
  return answer,results,link
