#HTML 렌더링
@app.route('/')
def home_page():
  if len(os.listdir('./static'))>0:
    for i in os.listdir('./static'):
      os.remove('./static/'+i)
  if len(os.listdir('./uploads'))>0:
    for i in os.listdir('./uploads'):
      os.remove('./uploads/'+i)
    return render_template('home.html')
  else:
	  return render_template('home.html')



#업로드 HTML 렌더링
