def upload_page():
	return render_template('upload.html')

#파일 업로드 처리
@app.route('/fileUpload', methods = ['GET', 'POST'])
def upload_file():
	if request.method == 'POST':
		f = request.files['file']
		#저장할 경로 + 파일명
		f.save('./uploads/' + secure_filename(f.filename))
		return render_template('check.html')

@app.route('/check',methods = ['GET'])
def show_image():
  if request.method == 'GET':
    file_list = os.listdir("./uploads")
    image_path = "./uploads/"+file_list[0]
    result,lis,img_link = load_to_result(image_path)
    if lis=='':
      return result
    lis = list(lis)
    answer = "<br>고객님께서 갖고 있는 두피 문제<br>"
    recommendation = "<div>"
    
    recommend = []
    for i in range(len(lis)):
      if int(lis[i])>=2:
        recommend.append(i)
    for r in recommend:
      answer+='<li>'+to_check[r]+'</li>'+'<br>'
    
    for r in recommend:
      problem,id, u, im, p=recom[r]['id'], recom[r]['name'], recom[r]['url'], recom[r]['img'], recom[r]['price']
      recommendation+= f"<li><img src = {im}><a href = {u}>제품 보러가기 click</a>><h4>{problem}에 좋은 제품   ->  제품이름 : {id}  가격 : {p}</h4></li>"

    
    recommendation+="</div>"

    return f'''<center>결과<br><img src = {img_link}><br>{result}<br><br>{answer}<br>추천상품 목록<br>{recommendation}
    <br><br>
    
    </div>

    <a href ="/">홈으로 돌아가기 click</a></center>'''
	
